SELECT first_name, last_name FROM players JOIN salaries ON salaries.player_id = players.id JOIN performances ON performances.player_id = players.id WHERE (salary / H) IN (SELECT (salary / H) AS "dollars per hit" FROM players JOIN salaries ON salaries.player_id = players.id JOIN performances ON performances.player_id = players.id WHERE performances.year = 2001 AND salaries.year = 2001 AND H > 0 ORDER BY "dollars per hit", first_name, last_name LIMIT 10) AND performances.year = 2001 AND salaries.year = 2001 AND H > 0
INTERSECT
SELECT first_name, last_name FROM players JOIN salaries ON salaries.player_id = players.id JOIN performances ON performances.player_id = players.id WHERE (salary / RBI) IN (SELECT (salary / RBI) AS "dollars per rbi" FROM players JOIN salaries ON salaries.player_id = players.id JOIN performances ON performances.player_id = players.id WHERE performances.year = 2001 AND salaries.year = 2001 AND RBI > 0 ORDER BY "dollars per rbi", first_name, last_name LIMIT 10) AND performances.year = 2001 AND salaries.year = 2001 AND RBI > 0 ORDER BY last_name;
